import React, { useState } from "react"
import { Link } from "react-router-dom"

const posts = require('../models/post');
const comments = require('../models/comment');

function Registry() {
    const [registryData, setRegistryData] = useState([])
    const [textTitle, setTextTitle] = useState("")
    const [textContent, setTextContent] = useState("")

    const addItem = (e) => {
        e.preventDefault();
        if(textTitle.length === 0 || textContent.length === 0) return;
        const newPost = {
            id: posts.posts.length ? posts.posts[posts.posts.length - 1].id + 1 : 1,
            title: textTitle,
            content: textContent
        };
        posts.posts.push(newPost);
        setRegistryData(posts.posts);
        setTextTitle("");
        setTextContent("");
    }

    const removeItem = (index) => {
        const newPosts = posts.posts.filter(position => position.id !== parseInt(index));
        posts.posts = newPosts;
        setRegistryData(posts.posts)
    }

    const editItem = (index) => {
        if(textTitle.length === 0 || textContent.length === 0) return;

        const post = posts.posts.find(position => position.id === parseInt(index));
        post.title = textTitle;
        post.content = textContent;
        const newPosts = posts.posts.filter(position => position.id !== parseInt(index));
        newPosts.push(post);
        newPosts.sort((a, b) => a.id > b.id ? 1 : a.id < b.id ? -1 : 0)
        posts.posts = newPosts;

        setRegistryData(posts.posts);
        setTextTitle("");
        setTextContent("");
    }

    return (
        <div className="registry">
            <h1>Registry</h1>
            <Link to="/">Click here to go to home</Link>
            <form onSubmit={addItem}>
                <input type="text" required value={textTitle} onChange={(e) => setTextTitle(e.target.value)} />
                <input type="text" required value={textContent} onChange={(e) => setTextContent(e.target.value)} />

                <input type="submit" value="Submit" />
            </form>
            <ul>
            {
                posts.posts.map((item, index) => {
                    return (
                        <div key={index}>
                            <h1>{item.title}</h1>
                            <p>{item.content}</p>
                            <div className="buttons">
                                <button onClick={() => removeItem(item.id)}>Remove</button>
                                <button onClick={() => editItem(item.id)}>Update</button>
                            </div>
                        </div>
                    )
                })
            }
            </ul>
        </div>
    )
}

export default Registry;
