import React, { useState } from "react"
import { Link, useParams } from "react-router-dom";

const data = require('../models/post');
const detail = require('../models/comment');

function Detail(index) {
    const { id } = useParams();
    const [postsData, setPostsData] = useState([]);

    return (
        <div className="details">
            <h1>Detail Post</h1>

            <h2>Title: </h2>
            <p>Content: </p>
            <ul>
            {
                postsData.map((item, index) => {
                    return (
                        <li key={index}>
                            <div className="texts">
                                <h4>Title: {item.title}</h4>
                                <p id="content">Content: {item.content}</p>
                            </div>
                            <div className="buttons">
                                <div><button id="edit" onClick={() => editItem(item.id)}>Update</button></div>
                                <div><button id="delete" onClick={() => removeItem(item.id)}>Remove</button></div>
                            </div>
                            <Link to={`/post/${item.id}`}>See Details</Link>
                        </li>
                    )
                })
            }
            </ul>
            <form onSubmit={addItem}>
                <p>Title: <input id="title" type="text" required value={textTitle} onChange={(e) => setTextTitle(e.target.value)} /></p>
                <p>Content: <input id="content" type="text" required value={textContent} onChange={(e) => setTextContent(e.target.value)} /></p>
                <input id="post" type="submit" value="Submit" />
            </form>
        </div>
    )
}

export default Detail;
