import React, { useState } from "react"
import { Link } from "react-router-dom"

const data = require('../models/post');
const comments = require('../models/comment');

function Posts() {
    const [postsData, setPostsData] = useState([])
    const [textTitle, setTextTitle] = useState("")
    const [textContent, setTextContent] = useState("")

    const addItem = (e) => {
        e.preventDefault();
        if(textTitle.length === 0 || textContent.length === 0) return;
        const newPost = {
            id: data.posts.length ? data.posts[data.posts.length - 1].id + 1 : 1,
            title: textTitle,
            content: textContent
        };
        data.posts.push(newPost);
        setPostsData(data.posts);
        setTextTitle("");
        setTextContent("");
    }

    const editItem = (index) => {
        if(textTitle.length === 0 || textContent.length === 0) return;
        const post = data.posts.find(position => position.id === parseInt(index));
        post.title = textTitle;
        post.content = textContent;
        const newPosts = data.posts.filter(position => position.id !== parseInt(index));
        newPosts.push(post);
        newPosts.sort((a, b) => a.id > b.id ? 1 : a.id < b.id ? -1 : 0)
        data.posts = newPosts;
        setPostsData(data.posts);
        setTextTitle("");
        setTextContent("");
    }

    const detailsItem = (index) => {
    }

    const removeItem = (index) => {
        const newPosts = data.posts.filter(position => position.id !== parseInt(index));
        data.posts = newPosts;
        setPostsData(data.posts)
    }

    return (
        <div className="posts">
            <h1>Posts</h1>
            <Link to="/">Click here to go to home</Link>
            <form onSubmit={addItem}>
                <input type="text" required value={textTitle} onChange={(e) => setTextTitle(e.target.value)} />
                <input type="text" required value={textContent} onChange={(e) => setTextContent(e.target.value)} />

                <input type="submit" value="Submit" />
            </form>
            <ul>
            {
                postsData.map((item, index) => {
                    return (
                        <li key={index}>
                            <div className="texts">
                                <h4>Title: {item.title}</h4>
                                <p>Content: {item.content}</p>
                            </div>
                            <div className="buttons">
                                <button onClick={() => editItem(item.id)}>Update</button>
                                <button onClick={() => removeItem(item.id)}>Remove</button>
                                <button onClick={() => detailsItem(item.id)}>Details</button>
                            </div>
                            <div>
                                <form onSubmit={addItem}>
                                    <div>
                                        <input type="text" required value={textTitle} onChange={(e) => setTextTitle(e.target.value)} />
                                    </div>
                                    <div>
                                        <input type="text" required value={textContent} onChange={(e) => setTextContent(e.target.value)} />
                                    </div>

                                    <input type="submit" value="Submit" />
                                </form>
                            </div>
                        </li>
                    )
                })
            }
            </ul>
        </div>
    )
}

export default Posts;
