import React, { useState } from "react"
import { Link, useParams } from "react-router-dom";

const data = require('../models/post');
const detail = require('../models/comment');

function Detail(index) {
    const { id } = useParams();
    const [commentsDetail, setCommentsDetail] = useState([]);
    const [textComment, setTextComment] = useState("");

    const post = data.posts.find(position => position.id === parseInt(index));
    var comment = [];
    for (const findComment of detail.comments) {
        if (findComment.id === id) {
            comment.push(findComment);
        }
    }
    setCommentsDetail(comment);

    const addComment = (index) => {

    }

    return (
        <div className="details">
            <h1>Detail Post</h1>

            <h2>Title: {post.title}</h2>
            <p>Content: {post.body}</p>
            <ul>
            {
                commentsDetail.map((item, index) => {
                    return (
                        <li key={index}>
                            <div className="texts">
                                <p id="comment">Content: {item.content}</p>
                            </div>
                        </li>
                    )
                })
            }
            </ul>
            <form onSubmit={addComment}>
                <p>Comment: <input id="comment" type="text" required value={textComment} onChange={(e) => setTextComment(e.target.value)} /></p>
                <input id="post" type="submit" value="Submit" />
            </form>
            <Link to="/index">See All Posts</Link>
        </div>
    )
}

export default Detail;
